'use strict';

var TIMEZONE_OFFSET = (new Date()).getTimezoneOffset();
var MS_OF_SECOND = 1000;
var MS_OF_MINUTE = 60000;
var MS_OF_HOUR = 3600000;
var MS_OF_DAY = 86400000;

exports.MS_OF_DAY = MS_OF_DAY;
exports.MS_OF_HOUR = MS_OF_HOUR;
exports.MS_OF_MINUTE = MS_OF_MINUTE;
exports.MS_OF_SECOND = MS_OF_SECOND;
exports.TIMEZONE_OFFSET = TIMEZONE_OFFSET;
