'use strict';

var isNil = require('celia/isNil');

function startOfTime(date, ms) {
  var input = +date;
  return input - input % ms;
}

function startOfDate(date, y, m, d) {
  return +new Date(
    y || date.getFullYear(),
    isNil(m) ? date.getMonth() : m,
    d || date.getDate()
  );
}

exports.startOfDate = startOfDate;
exports.startOfTime = startOfTime;
