'use strict';

var isObject = require('./isObject.js');
var getUid = require('./getUid.js');

function getEasyHash (value) {
  return isObject(value)
    ? 'o' + getUid(value)
    : (typeof value)[0] + value;
}

module.exports = getEasyHash;
